mapper.select:
SELECT * 
FROM adminuser 
WHERE userid = #{query} OR telno = #{query}

SELECT userid 
FROM adminuser 
WHERE email = #{email} AND username = #{userName}

mapper.update:
UPDATE adminuser
username = #{username}, email = #{email}, telno = #{telno}, postcode = #{postcode}, addr1 = #{addr1}, addr2 = #{addr2}, usertype = #{usertype}
WHERE userid = #{userid}

UPDATE adminuser
SET password = #{newPassword}
WHERE userid = #{userId}
