mapper.select:
SELECT p.row_no, p.col_no, p.seat_no, p.seat_price, COALESCE(COUNT(DISTINCT b.bid_telno), 0) AS total_bidders, COALESCE(MAX(b.bid_amount), 0) AS current_bid_amount FROM seatprice AS p LEFT JOIN bids AS b ON p.seat_no = b.seat_no AND p.match_no = b.match_no WHERE p.seat_no IN
#{seatNo}
AND p.match_no = #{matchNumber} GROUP BY p.row_no, p.col_no, p.seat_no, p.seat_price ORDER BY CAST(p.seat_no AS UNSIGNED) ASC;

mapper.update:
UPDATE bids
SET bid_paid = 'Y',
    bid_tid = #{tid},
    bid_pay_method = #{payMethod}
WHERE bid_telno = #{telno}
AND bid_won = 'Y'
